import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

 // Import useHistory from React Router
import './Teacher.css';

const Teacher = () => {
  const [isLogin, setIsLogin] = useState(true);
  const navigate = useNavigate(); // Initialize useHistory hook

  const toggleForm = () => {
    setIsLogin(!isLogin);
  };

  // Handle form submission
  const handleLogin = (e) => {
    e.preventDefault();
    // Add your login logic here (e.g., check credentials)

    // Assuming login is successful, navigate to profile page
    navigate
             ('/profile');
  };
  

  return (
    
    <div className='teacher'>
     <h1>Teacher Details</h1>
      <div className="form-container">
        {isLogin ? <LoginForm toggleForm={toggleForm} handleLogin={handleLogin} /> : <SignUpForm toggleForm={toggleForm} />}
      </div>
      
    
    </div>
   
  );
};

const LoginForm = ({ toggleForm, handleLogin }) => (
  <form className="form" onSubmit={handleLogin}>
    <h2>Login</h2>
    <label>
     
      Email_id: <input type="email" name="email" required />
    </label>
    <label>
       Password:
      <input type="password" name="password" required/>
    </label>
    
    <button type="submit">Login</button>
    <p>
      Don't have an account? <span className="link" onClick={toggleForm}>Sign Up</span>
    </p>
  </form>
);

const SignUpForm = ({ toggleForm }) => (
  <form className="form">
    <h2>Sign Up</h2>
    <label>
      Username:
      <input type="text" name="username" required />
    </label>
    <label>
     Teacher_id:
      <input type="text" name="permanent roll:no:" required />
    </label>
  
    <label>
    Email_id:
      <input type="email" name="email" required />
    </label>
    <label>
    Password:
      <input type="password" name="password" required />
    </label>
    <label>
    Department:
      <input type="text" name="Branch" required />
    </label>
    <label>
    Mobile no:
      <input type="text" name="Mobile Number" required />
    </label>
    <button type="submit">Sign Up</button>
    <p>
      Already have an account? <span className="link" onClick={toggleForm}>Login</span>
    </p>
  </form>
);

export default Teacher;
